import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import AccountDetails from './AccountDetails';

function Header() {

    const location = useLocation();


    return (
        <>
            <header>
                <div class="logo">
                    <Link to="/sites" className={location.pathname === '/sites' ? 'active' : '' || location.pathname === '/' ? 'active' : '' }>
                        <img class="logo-white" src="https://huberway.it/assets/imgs/logo/huberway_.webp" />
                    </Link>
                </div>
                <div class="menu">
                    <ul>
                        <li>
                            <Link className={location.pathname === '/community' ? 'active' : ''} to="/community"><i class="fal fa-chart-network"></i> Community</Link>
                        </li>
                        <li>
                            <Link className={location.pathname === '/teams' ? 'active' : ''} to="/teams"><i class="fal fa-users"></i> Teams</Link>
                        </li>
                    </ul>
                </div>
                <AccountDetails />

            </header>
        </>
    );

}


export default Header;
