import React, { useEffect, useState, useRef } from "react";
import { useNavigate, Link } from "react-router-dom";
import { getAccountDetails, getClientApps } from "../backend/api";
import Header from "../components/Header";

const Pricing = () => {
  const [user, setUser] = useState({ email: "", name: "", id: null });
  const navigate = useNavigate();
  const menuRef = useRef(null);

  useEffect(() => {
    getAccountDetails()
      .then((data) => {
        setUser({
          email: data.data.email,
          name: data.data.username,
          id: data.data.id,
        });
      })
      .catch((error) => {
        console.error("Errore nel recupero dei dettagli dell'account:", error);
        navigate("/account/login");
      });
  }, [navigate]);

  const handleLogout = () => {
    // Cancella tutti i cookie sul dominio .huberway.com
    const clearCookies = () => {
      const cookies = document.cookie.split(";");

      cookies.forEach((cookie) => {
        const [name] = cookie.split("=");
        document.cookie = `${name}=; Path=/; Domain=.huberway.com; Expires=Thu, 01 Jan 1970 00:00:00 UTC; Secure; SameSite=Strict`;
      });
    };

    // Esegui la cancellazione dei cookie
    clearCookies();

    navigate("/account/login");
  };

  return (
    <div className="dashboard-container">
      <Header></Header>

      <main className="pricing dynamic">
        <sidebar>
          <ul>
            <li>
              <span>HubConnect</span>
            </li>
          </ul>
        </sidebar>
        <section></section>
      </main>
    </div>
  );
};

export default Pricing;
