import React, { useEffect, useState } from 'react';
import SidebarSites from '../components/SidebarSites';
import $ from 'jquery';
import 'datatables.net';
import { getCookie, loadScriptsSequentially } from '../utils';

function Sites() {
    const [sites, setSites] = useState([]);

    useEffect(() => {
        const authToken = '774766dc33eb73f3abcbb0b19a4f46f4';
        caricaSiti(authToken);

        // Carica jQuery e altri script necessari
        const script = document.createElement('script');
        script.src = "https://app.huberway.org/assets/plugins/jquery/jquery-3.2.1.min.js?v=" + Date.now();
        script.onload = () => {
            loadScriptsSequentially([
                "https://app.huberway.org/assets/plugins/pace/pace.min.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/liga.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/modernizr.custom.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/jquery-ui/jquery-ui.min.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/popper/umd/popper.min.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/bootstrap/js/bootstrap.min.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/jquery/jquery-easy.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/jquery-unveil/jquery.unveil.min.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/jquery-ios-list/jquery.ioslist.min.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/jquery-actual/jquery.actual.min.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/jquery-scrollbar/jquery.scrollbar.min.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/jquery-datatable/media/js/jquery.dataTables.min.js",
                "https://app.huberway.org/assets/plugins/jquery-datatable/extensions/TableTools/js/dataTables.tableTools.min.js",
                "https://app.huberway.org/assets/plugins/jquery-datatable/media/js/dataTables.bootstrap.js",
                "https://app.huberway.org/assets/plugins/jquery-datatable/extensions/Bootstrap/jquery-datatable-bootstrap.js",
                "https://app.huberway.org/assets/plugins/datatables-responsive/js/datatables.responsive.js",
                "https://app.huberway.org/assets/plugins/select2/js/select2.full.min.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/classie/classie.js?v=" + Date.now(),
                "https://app.huberway.org/assets/plugins/jquery-validation/js/jquery.validate.min.js?v=" + Date.now(),
                "https://cdn.jsdelivr.net/npm/canvas-confetti@1.4.0/dist/confetti.browser.js?v=" + Date.now(),
            ]).then(() => {
                // Inizializza DataTables solo dopo che tutti gli script sono caricati
                // initializeDataTable();
            });
        };
        document.body.appendChild(script);

        return () => {
            document.body.removeChild(script);
        };
    }, []);

    const caricaSiti = (authToken) => {
        $.ajax({
            url: 'https://api.huberway.com/WebsiteController/websites',
            type: 'POST',
            data: JSON.stringify({ auth_token: authToken }),
            contentType: 'application/json',
            success: function (response) {
                if (response.success === false) {
                    console.error('Errore nel caricamento dei siti.');
                } else {
                    setSites(response.data);
                    // Inizializza DataTables dopo che i siti sono stati caricati
                    setTimeout(function () {
                        initializeDataTable();
                    }, 200);

                }
            },
            error: function (xhr, status, error) {
                console.error('Errore nella richiesta', error);
            }
        });
    };

    const initializeDataTable = () => {
        const table = $('#basicTable').DataTable({
            sDom: 't',
            destroy: true,
            paging: false,
            scrollCollapse: true,
            aoColumnDefs: [{ bSortable: false, aTargets: [0] }],
            order: [[1, 'desc']],
        });

        $('#basicTable').on('click', 'input[type=checkbox]', function () {
            if ($(this).is(':checked')) {
                $(this).closest('tr').addClass('selected');
            } else {
                $(this).closest('tr').removeClass('selected');
            }
        });

        return () => {
            if (table) {
                table.destroy(true);
            }
        };
    };

    const generaSparkline = (data) => {
        let larghezzaSVG = 95;
        let altezzaSVG = 13;
        let larghezzaBarra = 2;

        let maxVisite = Math.max(...data);

        return (
            <svg width={larghezzaSVG} height={altezzaSVG} viewBox={`0 0 ${larghezzaSVG} ${altezzaSVG}`}>
                {data.map((visite, index) => {
                    let altezzaBarra = (visite / maxVisite) * altezzaSVG;
                    let x = index * larghezzaBarra;
                    let y = altezzaSVG - altezzaBarra;
                    return <rect key={index} x={x} y={y} height={altezzaBarra} width="1" className="stats-sparkline__bar" />;
                })}
            </svg>
        );
    };

    const openSettings = (websiteId) => {
        console.log('Apri le impostazioni per il sito con ID:', websiteId);
    };

    return (
        <div className="app-content">
            <SidebarSites />
            <div className="content-app">
                <div className="content-header">
                    <h3>Sites</h3>
                    <button className="btn add-website">Add new site</button>
                </div>
                <div className="content-body">
                    <div className="table-responsive">
                        <table className="table table-hover" id="basicTable">
                            <thead>
                                <tr>
                                    <th style={{ width: '33%' }}>Site</th>
                                    <th style={{ width: '20%' }}>Plan</th>
                                    <th style={{ width: '29%' }}>Status</th>
                                    <th style={{ width: '15%' }}>Last Published</th>
                                    <th style={{ width: '15%' }}>Stats</th>
                                    <th style={{ width: '15%' }}>Actions</th>
                                </tr>
                            </thead>
                            <tbody id="site-list">
                                {sites.map((site) => (
                                    <tr key={site.website_id} onClick={() => openSettings(site.website_id)}>
                                        <td className="v-align-middle">
                                            <div className="site-thumb">
                                                <div className="site-preview">{site.name.charAt(0)}</div>
                                                <div className="site-content">
                                                    <div className="site-title">{site.name}</div>
                                                    <a href={`${site.scheme}://${site.host}`} target="_blank" rel="noopener noreferrer">
                                                        {site.host} <i className="fas fa-external-link"></i>
                                                    </a>
                                                </div>
                                            </div>
                                        </td>
                                        <td className="v-align-middle">
                                            <p>{site.plan || 'Free'}</p>
                                        </td>
                                        <td className="v-align-middle">
                                            <p>
                                                {site.status || 'Unknown'}
                                                <br />
                                                <a href="#">Checklist</a>
                                            </p>
                                        </td>
                                        <td className="v-align-middle">
                                            <p>{site.date || 'N/A'}</p>
                                        </td>
                                        <td className="v-align-middle">
                                            <div className="stats-sparkline-container" style={{ height: '13px', width: '100%' }}>
                                                {generaSparkline([6, 3, 12, 14, 10, 7, 12, 4, 6, 9, 2, 6, 10, 10, 7, 4, 3, 7, 7, 2, 5, 4, 1, 7, 11, 13, 5, 1, 11, 4, 0, 11, 9, 5, 12, 11, 8, 0, 10, 10, 14, 9, 11, 11, 14, 13, 13, 14])}
                                            </div>
                                        </td>
                                        <td className="v-align-middle">
                                            <div className="action">
                                                <a href="#">
                                                    <i className="fas fa-ellipsis-h"></i>
                                                    <ul className="dropdown">
                                                        <li>
                                                            <a href="#">Launch site</a>
                                                        </li>
                                                        <li>
                                                            <a href="#" onClick={() => openSettings(site.website_id)}>
                                                                Settings
                                                            </a>
                                                        </li>
                                                        <li>
                                                            <a href="#">Hosting</a>
                                                        </li>
                                                        <li>
                                                            <a href="#">Analytics</a>
                                                        </li>
                                                    </ul>
                                                </a>
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            <div className="content-site" style={{ display: 'none' }}>
                <div className="content-header-site">
                    <div className="site-thumb">
                        <div className="site-preview" id="site-letter"></div>
                        <div className="site-content">
                            <div className="site-title" id="site-name"></div>
                            <a href="" data-ajax="" target="_blank" id="site-host">
                                <i className="fas fa-external-link"></i>
                            </a>
                        </div>
                    </div>
                    <button className="btn add-website">Close</button>
                </div>
                <div className="content-body-site"></div>
            </div>
        </div>
    );
}

export default Sites;
