import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import Sites from './pages/Sites';
// import NotFoundPage from './pages/NotFoundPage';
import useLoadScripts from './components/useLoadScripts';
import { getCookie } from './utils';

function App() {
    const navigate = useNavigate();

    useEffect(() => {
        const authToken = getCookie('auth_token');

        if (!authToken) {
            // Reindirizza alla pagina di login su app.huberway.org se non c'è il token
            window.location.href = 'https://app.huberway.org/account/login';
        } else {
            // Se vuoi provare localmente, reindirizza a una pagina locale fittizia
            console.log('Utente autenticato con authToken:', authToken);
        }
    }, [navigate]);

    return (
        <div className="App">
            <Routes>
                <Route exact path="/" element={<Sites />} />
                {/* Rotta di fallback per gestire le pagine non trovate */}
                {/* <Route path="*" element={<NotFoundPage />} /> */}
            </Routes>
            <useLoadScripts />
        </div>
    );
}

export default App;
