import React, { useState } from 'react';

const Step2 = ({ formData, handleInputChange, handleNext, handleBack }) => {
  const [otp, setOtp] = useState(Array(6).fill(''));

  const handleOtpChange = (e, index) => {
    const newOtp = [...otp];
    newOtp[index] = e.target.value;
    setOtp(newOtp);
  };

  const verifyOtp = async () => {
    const otpValue = otp.join('');
    const response = await fetch('https://api.huberway.com/auth/verify-mail', {
      method: 'POST',
      body: JSON.stringify({ email: formData.email, otp: otpValue }),
      headers: {
        'Content-Type': 'application/json',
      },
    });
    const result = await response.json();
    return result.type !== 'failed';
  };

  return (
    <div>
      <button onClick={handleBack} className="back">
        Back
      </button>
      <h1>Check your email address</h1>
      <h5>Enter the verification code sent to: <b>{formData.email}</b></h5>
      <div className="otp-form">
        {otp.map((value, index) => (
          <input
            key={index}
            className="otp-field"
            type="text"
            value={value}
            maxLength={1}
            onChange={(e) => handleOtpChange(e, index)}
          />
        ))}
      </div>
      <button onClick={() => verifyOtp() && handleNext()} disabled={otp.join('').length < 6} className="btn btn-primary">
        Next
      </button>
    </div>
  );
};

export default Step2;
