import React from 'react';
import { useLocation } from 'react-router-dom';
import { Helmet } from 'react-helmet';

function DynamicTitle() {
    const location = useLocation();

    const getPageMeta = (pathname) => {
        switch (pathname) {
            case '/sites':
                return {
                    title: 'Sites - Huberway.com',
                    description: 'Manage and optimize your websites efficiently with Huberway\'s comprehensive site management tools.',
                    keywords: 'Sites, Huberway, Website Management, Digital Tools, Optimization',
                    image: 'https://app.huberway.org/assets/images/sites-image.png', // Aggiungi l'URL dell'immagine
                    url: 'https://app.huberway.org/sites',
                };
            case '/domains':
                return {
                    title: 'Domains - Huberway.com',
                    description: 'Explore and manage your domains with ease on Huberway, your trusted partner in domain management.',
                    keywords: 'Domains, Huberway, Domain Management, Digital Tools, Domain Services',
                    image: 'https://app.huberway.org/assets/images/domains-image.png',
                    url: 'https://app.huberway.org/domains',
                };
            case '/themes':
                return {
                    title: 'Themes - Huberway.com',
                    description: 'Browse and customize themes to create a unique online presence with Huberway.',
                    keywords: 'Themes, Huberway, Website Themes, Customization, Digital Tools',
                    image: 'https://app.huberway.org/assets/images/themes-image.png',
                    url: 'https://app.huberway.org/themes',
                };
            case '/plugins':
                return {
                    title: 'Plugins - Huberway.com',
                    description: 'Enhance your website functionality with a variety of plugins available on Huberway.',
                    keywords: 'Plugins, Huberway, Website Plugins, Functionality, Digital Tools',
                    image: 'https://app.huberway.org/assets/images/plugins-image.png',
                    url: 'https://app.huberway.org/plugins',
                };
            case '/community':
                return {
                    title: 'Community - Huberway.com',
                    description: 'Join the Huberway community to collaborate, share knowledge, and grow your network.',
                    keywords: 'Community, Huberway, Networking, Collaboration, Knowledge Sharing',
                    image: 'https://app.huberway.org/assets/images/community-image.png',
                    url: 'https://app.huberway.org/community',
                };
            case '/teams':
                return {
                    title: 'Teams - Huberway.com',
                    description: 'Organize and manage your teams effectively with Huberway\'s team collaboration tools.',
                    keywords: 'Teams, Huberway, Team Management, Collaboration, Digital Tools',
                    image: 'https://app.huberway.org/assets/images/teams-image.png',
                    url: 'https://app.huberway.org/teams',
                };
            case '/settings':
                return {
                    title: 'Settings - Huberway.com',
                    description: 'Customize your Huberway account settings and preferences to suit your needs.',
                    keywords: 'Settings, Huberway, Account Management, Preferences, Digital Tools',
                    image: 'https://app.huberway.org/assets/images/settings-image.png',
                    url: 'https://app.huberway.org/settings',
                };
            default:
                return {
                    title: 'Sites - Huberway.com',
                    description: 'Manage and optimize your websites efficiently with Huberway\'s comprehensive site management tools.',
                    keywords: 'Sites, Huberway, Website Management, Digital Tools, Optimization',
                    image: 'https://app.huberway.org/assets/images/sites-image.png', // Aggiungi l'URL dell'immagine
                    url: 'https://app.huberway.org/sites',
                };
        }
    };


    const { title, description, keywords, image, url } = getPageMeta(location.pathname);

    return (
        <Helmet>
            {/* Meta tag di base */}
            <title>{title}</title>
            <meta name="description" content={description} />
            <meta name="keywords" content={keywords} />
            <meta name="author" content="Renatia" />
            <meta name="robots" content="index, follow" />
            <link rel="canonical" href={url} />

            {/* Meta tag per i social network - Open Graph */}
            <meta property="og:title" content={title} />
            <meta property="og:description" content={description} />
            <meta property="og:type" content="website" />
            <meta property="og:url" content={url} />
            <meta property="og:image" content={image} />
            <meta property="og:site_name" content="Huberway" />

            {/* Meta tag per Twitter */}
            <meta name="twitter:card" content="summary_large_image" />
            <meta name="twitter:title" content={title} />
            <meta name="twitter:description" content={description} />
            <meta name="twitter:image" content={image} />
            <meta name="twitter:site" content="@huberway" />

            {/* Meta tag di compatibilit√† e mobile */}
            <meta name="viewport" content="width=device-width, initial-scale=1" />
            <meta http-equiv="X-UA-Compatible" content="IE=edge" />
        </Helmet>
    );
}

export default DynamicTitle;
