import React, { useEffect, useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAccountDetails, getClientApps } from '../backend/api';

const Dashboard = () => {
    const [user, setUser] = useState({ email: '', name: '', id: null });
    const [clientApps, setClientApps] = useState([]);
    const [menuOpen, setMenuOpen] = useState(false);
    const navigate = useNavigate();
    const menuRef = useRef(null);

    useEffect(() => {
        getAccountDetails()
            .then(data => {
                setUser({ email: data.data.email, name: data.data.username, id: data.data.id });
            })
            .catch(error => {
                console.error('Errore nel recupero dei dettagli dell\'account:', error);
                navigate('/account/login');
            });
    }, [navigate]);

    useEffect(() => {
        if (user.id) {
            getClientApps()
                .then(apps => {
                    setClientApps(apps.filter(app => app.user_id === user.id));
                })
                .catch(err => console.error('Errore nel recupero delle app:', err));
        }
    }, [user.id]);

    const handleLogout = () => {// Cancella tutti i cookie sul dominio .huberway.com
        const clearCookies = () => {
            const cookies = document.cookie.split(';');

            cookies.forEach((cookie) => {
                const [name] = cookie.split('=');
                document.cookie = `${name}=; Path=/; Domain=.huberway.com; Expires=Thu, 01 Jan 1970 00:00:00 UTC; Secure; SameSite=Strict`;
            });
        };

        // Esegui la cancellazione dei cookie
        clearCookies();

        navigate('/account/login');
    };

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    const handleClickOutside = (event) => {
        if (menuRef.current && !menuRef.current.contains(event.target)) {
            setMenuOpen(false);
        }
    };

    useEffect(() => {
        document.addEventListener('mousedown', handleClickOutside);
        return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const huberwayLinks = [
        {
            name: 'HubConnect',
            description: 'Centralized management of CRM, marketing, and chat',
            url: 'https://app.huberway.com',
            icons: [
                "https://dev.huberway.com/icon/sales.svg",
                "https://dev.huberway.com/icon/marketing.svg",
                "https://dev.huberway.com/icon/smartchat.svg"
            ]
        },
        {
            name: 'ContentFlow',
            description: 'Content management, E-Commerce and Web App Development',
            url: '#',
            icons: [
                "https://dev.huberway.com/icon/content.svg",
            ]
        },
        {
            name: 'Web Analytics',
            description: 'Detailed analysis of traffic and conversions',
            url: 'https://analytics.huberway.com'
        }
    ];

    return (
        <div className="dashboard-container">
            <header className="header">
                <img src="https://cdn.huberway.com/site/logo-dark.svg" alt="Huberway Logo" className="logo" />
                <div className="user-menu" ref={menuRef}>
                    <div className="user-info" onClick={toggleMenu}>
                        Hello, {user.name} <i className='fal fa-chevron-down'></i>
                    </div>
                    {menuOpen && (
                        <div className="dropdown-menu show">
                            <ul>
                                <li onClick={() => navigate('/settings')}><i className='fal fa-cog'></i> Settings</li>
                                <li onClick={() => navigate('/billing')}><i className='fal fa-credit-card'></i> Billing</li>
                                <hr className='hr-dash'></hr>
                                <li onClick={handleLogout}><i className='fal fa-sign-out'></i> Logout</li>
                            </ul>
                        </div>
                    )}
                </div>
            </header>
            <main className="software-list dynamic">
                <section>
                    <h2>Apps</h2>
                    <div class="grid-container">
                        {huberwayLinks.map((software) => (
                            <a
                                key={software.name}
                                href={software.url}
                                className={`software-card dynamic-card ${software.url === '#' ? 'coming-soon' : ''}`}
                                onClick={(e) => software.url === '#' && e.preventDefault()} // Previene il click se in Coming Soon
                            >
                                <div className="card-content">
                                    <h3>
                                        {software.icons && software.icons.map((icon, index) => (
                                            <img key={index} src={icon} alt="" style={{ height: "30px", marginRight: "5px" }} />
                                        ))}
                                        {software.name}
                                    </h3>
                                    <p>{software.description}</p>
                                </div>
                                {software.url === '#' && <span className="coming-soon-badge">Coming Soon</span>}
                                <div className="card-hover-effect"></div>
                            </a>
                        ))}

                    </div>
                </section>
                <section>
                    <h2>My Apps</h2>
                    <div class="grid-container">
                        {clientApps.map((software) => (
                            <a key={software.name} href={software.url} className="software-card dynamic-card">
                                <div className="card-content">
                                    <h3>{software.name}</h3>
                                    <p>Access {software.name} tools and services</p>
                                </div>
                                <div className="card-hover-effect"></div>
                            </a>
                        ))}
                    </div>
                </section>
            </main>
        </div>
    );
};

export default Dashboard;
