import React, { useState, useEffect } from 'react';

const Step1 = ({ formData, handleInputChange, handleNext }) => {
    const [emailValid, setEmailValid] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [otpSent, setOtpSent] = useState(false);

    const validateEmail = async () => {
        if (formData.email && /\S+@\S+\.\S+/.test(formData.email)) {
            // Make API request to check if the email is valid
            try {
                const response = await fetch('https://api.huberway.com/auth/is-new-email', {
                    method: 'POST',
                    body: JSON.stringify({ email: formData.email }),
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });
                const result = await response.json();
                if (result.type === 'registered') {
                    setErrorMessage('This email is already registered.');
                    setEmailValid(false);
                } else {
                    setEmailValid(true);
                    setErrorMessage('');
                    sendOtp(formData.email);  // Send OTP after email is validated
                }
            } catch (error) {
                setErrorMessage('Error while verifying email.');
                setEmailValid(false);
            }
        } else {
            setErrorMessage('Invalid email format.');
            setEmailValid(false);
        }
    };

    useEffect(() => {
        validateEmail();
    }, [formData.email]);


    const sendOtp = async (email) => {
        // Make API request to send OTP to the user
        try {
            const response = await fetch('https://api.huberway.com/auth/send-mail-verify', {
                method: 'POST',
                body: JSON.stringify({ email }),
                headers: {
                    'Content-Type': 'application/json',
                },
            });
            const result = await response.json();
            if (result.success) {
                setOtpSent(true);
                setErrorMessage('');
            } else {
                setErrorMessage('Failed to send OTP. Please try again.');
                setOtpSent(false);
            }
        } catch (error) {
            setErrorMessage('Error while sending OTP.');
            setOtpSent(false);
        }
    };
    return (

        <>
            <div className="row">
                <div className="col-md-12 head-register">
                    <h1>Create your free account</h1>
                    <h5>100% free. No credit card needed.</h5>
                </div>
            </div>

            <div className="row mb-20">
                <div className="col-md-12">
                    <label>Email address</label>
                    <input type="email" name="email" value={formData.email} onChange={handleInputChange} className="form-control" required />
                    {!emailValid && formData.email && <div className="text-danger">This email is already registered</div>}
                </div>
            </div>
            <button onClick={handleNext} disabled={!emailValid} className="btn btn-primary btn-cons m-t-10 btn-login">
                Verify Email
            </button>
            {/* Information message after OTP is sent */}
            {otpSent && <p>OTP has been sent to your email address. Please check your inbox.</p>}


            <p class="text-information">
                Weâ€™re committed to your privacy. Huberway uses the information you provide to us to contact you about our relevant content, products, and services. You may unsubscribe from these communications at any time. For more information, check out our <a href="#">Privacy Policy</a>
            </p>
        </>
    );
};

export default Step1;
