import React, { useEffect, useState } from 'react';
import SidebarSites from '../components/SidebarSites';
import $ from 'jquery'; // Assicurati di avere jQuery installato
import 'datatables.net'; // Assicurati di avere DataTables installato

function Sites() {
    const [sites, setSites] = useState([]);

    useEffect(() => {
        // Recupera il token di autenticazione
        const authToken = getCookie('auth_token');

        // Carica i siti all'inizio
        caricaSiti(authToken);

        // Inizializza la tabella al montaggio del componente
        initBasicTable();

        // Cleanup function to reset DataTable
        return () => {
            $('#basicTable').DataTable().destroy(true);
        };
    }, []);

    const caricaSiti = (authToken) => {
        $.ajax({
            url: 'https://api.huberway.com/WebsiteController/websites',
            type: 'POST',
            data: JSON.stringify({ auth_token: authToken }),
            contentType: 'application/json',
            success: function (response) {
                if (response.success === false) {
                    console.error('Errore nel caricamento dei siti.');
                } else {
                    setSites(response.data);
                }
            },
            error: function (xhr, status, error) {
                console.error('Errore nella richiesta', error);
            }
        });
    };

    const generaSparkline = (data) => {
        let larghezzaSVG = 95;
        let altezzaSVG = 13;
        let larghezzaBarra = 2;

        let maxVisite = Math.max(...data);

        return (
            <svg width={larghezzaSVG} height={altezzaSVG} viewBox={`0 0 ${larghezzaSVG} ${altezzaSVG}`}>
                {data.map((visite, index) => {
                    let altezzaBarra = (visite / maxVisite) * altezzaSVG;
                    let x = index * larghezzaBarra;
                    let y = altezzaSVG - altezzaBarra;
                    return <rect key={index} x={x} y={y} height={altezzaBarra} width="1" className="stats-sparkline__bar" />;
                })}
            </svg>
        );
    };

    const initBasicTable = () => {
        $('#basicTable').DataTable({
            sDom: 't',
            destroy: true,
            paging: false,
            scrollCollapse: true,
            aoColumnDefs: [{ bSortable: false, aTargets: [0] }],
            order: [[1, 'desc']],
        });

        $('#basicTable input[type=checkbox]').on('click', function () {
            if ($(this).is(':checked')) {
                $(this).closest('tr').addClass('selected');
            } else {
                $(this).closest('tr').removeClass('selected');
            }
        });
    };

    const openSettings = (websiteId) => {
        // Funzione per aprire le impostazioni del sito
        console.log('Apri le impostazioni per il sito con ID:', websiteId);
    };

    return (
        <div className="app-content">
            <SidebarSites />
            <div className="content-app">
                <div className="content-header">
                    <h3>Sites</h3>
                    <button className="btn add-website">Add new site</button>
                </div>
                <div className="content-body">
                    <div className="table-responsive">
                        <table className="table table-hover" id="basicTable">
                            <thead>
                                <tr>
                                    <th style={{ width: '33%' }}>Site</th>
                                    <th style={{ width: '20%' }}>Plan</th>
                                    <th style={{ width: '29%' }}>Status</th>
                                    <th style={{ width: '15%' }}>Last Published</th>
                                    <th style={{ width: '15%' }}>Stats</th>
                                    <th style={{ width: '15%' }}>Actions</th>
                                </tr>
                            </thead>
                            <tbody id="site-list">
                                {sites.map((site) => (
                                    <tr key={site.website_id} onClick={() => openSettings(site.website_id)}>
                                        <td className="v-align-middle">
                                            <div className="site-thumb">
                                                <div className="site-preview">{site.name.charAt(0)}</div>
                                                <div className="site-content">
                                                    <div className="site-title">{site.name}</div>
                                                    <a href={`${site.scheme}://${site.host}`} target="_blank" rel="noopener noreferrer">
                                                        {site.host} <i className="fas fa-external-link"></i>
                                                    </a>
                                                </div>
                                            </div>
                                        </td>
                                        <td className="v-align-middle">
                                            <p>{site.plan || 'Free'}</p>
                                        </td>
                                        <td className="v-align-middle">
                                            <p>
                                                {site.status || 'Unknown'}
                                                <br />
                                                <a href="#">Checklist</a>
                                            </p>
                                        </td>
                                        <td className="v-align-middle">
                                            <p>{site.date || 'N/A'}</p>
                                        </td>
                                        <td className="v-align-middle">
                                            <div className="stats-sparkline-container" style={{ height: '13px', width: '100%' }}>
                                                {generaSparkline([6, 3, 12, 14, 10, 7, 12, 4, 6, 9, 2, 6, 10, 10, 7, 4, 3, 7, 7, 2, 5, 4, 1, 7, 11, 13, 5, 1, 11, 4, 0, 11, 9, 5, 12, 11, 8, 0, 10, 10, 14, 9, 11, 11, 14, 13, 13, 14])}
                                            </div>
                                        </td>
                                        <td className="v-align-middle">
                                            <div className="action">
                                                <a href="#">
                                                    <i className="fas fa-ellipsis-h"></i>
                                                    <ul className="dropdown">
                                                        <li>
                                                            <a href="#">Launch site</a>
                                                        </li>
                                                        <li>
                                                            <a href="#" onClick={() => openSettings(site.website_id)}>
                                                                Settings
                                                            </a>
                                                        </li>
                                                        <li>
                                                            <a href="#">Hosting</a>
                                                        </li>
                                                        <li>
                                                            <a href="#">Analytics</a>
                                                        </li>
                                                    </ul>
                                                </a>
                                            </div>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            <div class="content-site" style="display:none;">
                <div class="content-header-site">
                    <div class="site-thumb">
                        <div class="site-preview" id="site-letter"></div>
                        <div class="site-content">
                            <div class="site-title" id="site-name"></div>
                            <a href="" data-ajax="" target="_blank" id="site-host"><i class="fas fa-external-link"></i></a>
                        </div>
                    </div>
                    <button class="btn add-website">Close</button>
                </div>
                <div class="content-body-site">

                </div>
            </div>
        </div>
    );
}

export default Sites;
