import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAccountDetails, getClientApps } from '../backend/api';

const Dashboard = () => {
    const [user, setUser] = useState({ email: '', name: '', id: null });
    const [clientApps, setClientApps] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        getAccountDetails()
            .then(data => {
                setUser({ email: data.data.email, name: data.data.username, id: data.data.id });
            })
            .catch(error => {
                console.error('Errore nel recupero dei dettagli dell\'account:', error);
                navigate('/account/login');
            });
    }, [navigate]);

    useEffect(() => {
        if (user.id) {
            getClientApps()
                .then(apps => {
                    setClientApps(apps.filter(app => app.user_id === user.id));
                })
                .catch(err => console.error('Errore nel recupero delle app:', err));
        }
    }, [user.id]);

    const huberwayLinks = [
        { name: 'CRM', url: 'https://app.huberway.com' },
        { name: 'Email Marketing', url: 'https://campaign.huberway.com' },
        { name: 'Web Analytics', url: 'https://analytics.huberway.com' }
    ];


    return (
        <div className="dashboard-container">
            <header className="header">
                <img src="/logo-huberway.png" alt="Huberway Logo" className="logo" />
                <div className="user-info">
                    Hello, {user.name}
                </div>
            </header>
            <main className="software-list dynamic">
                <section>
                    <h2>Apps</h2>
                    {huberwayLinks.map((software) => (
                        <a key={software.name} href={software.url} className="software-card dynamic-card">
                            <div className="card-content">
                                <h3>{software.name}</h3>
                                <p>Access {software.name} tools and services</p>
                            </div>
                            <div className="card-hover-effect"></div>
                        </a>
                    ))}
                </section>
                <section>
                    <h2>My Apps</h2>
                    {clientApps.map((software) => (
                        <a key={software.name} href={software.url} className="software-card dynamic-card">
                            <div className="card-content">
                                <h3>{software.name}</h3>
                                <p>Access {software.name} tools and services</p>
                            </div>
                            <div className="card-hover-effect"></div>
                        </a>
                    ))}
                </section>
            </main>
        </div>
    );
};

export default Dashboard;
