import React, { useState, useEffect } from 'react';

const Step1 = ({ formData, handleInputChange, handleNext }) => {
  const [emailValid, setEmailValid] = useState(false);

  const validateEmail = async () => {
    if (formData.email && /\S+@\S+\.\S+/.test(formData.email)) {
      // Make API request to check if the email is valid
      const response = await fetch('https://api.huberway.com/auth/is-new-email', {
        method: 'POST',
        body: JSON.stringify({ email: formData.email }),
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const result = await response.json();
      setEmailValid(result.type !== 'registered');
    }
  };

  useEffect(() => {
    validateEmail();
  }, [formData.email]);

  return (
    <div >
      <h1>Create your free account</h1>
      <h5>100% free. No credit card needed.</h5>
      <label>Email address</label>
      <input type="email" name="email" value={formData.email} onChange={handleInputChange} className="form-control" required />
      {!emailValid && formData.email && <div className="text-danger">This email is already registered</div>}
      <button onClick={handleNext} disabled={!emailValid} className="btn btn-primary">
        Verify Email
      </button>
    </div>
  );
};

export default Step1;
