import React, { useState, useEffect } from 'react';
import Step1 from './Step1';
import Step2 from './Step2';
import Step3 from './Step3';
import Step4 from './Step4';
import Step5 from './Step5';
import Step6 from './Step6';
import Step7 from './Step7';
import Step8 from './Step8';
import Step9 from './Step9';
import Step10 from './Step10';
import Step11 from './Step11';
import Step12 from './Step12';
import Step13 from './Step13';
import ProgressBar from './ProgressBar';

const RegisterForm = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [progress, setProgress] = useState(0);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    otp: '',
    companyName: '',
    companyWebsite: '',
  });

  useEffect(() => {
    setProgress((currentStep / 13) * 100);
  }, [currentStep]);

  const handleNext = () => {
    setCurrentStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setCurrentStep((prevStep) => (prevStep > 1 ? prevStep - 1 : 1));
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const renderStep = () => {
    switch (currentStep) {
      case 1:
        return <Step1 formData={formData} handleInputChange={handleInputChange} handleNext={handleNext} />;
      case 2:
        return <Step2 formData={formData} handleInputChange={handleInputChange} handleNext={handleNext} handleBack={handleBack} />;
      case 3:
        return <Step3 formData={formData} handleInputChange={handleInputChange} handleNext={handleNext} handleBack={handleBack} />;
      case 4:
        return <Step4 formData={formData} handleInputChange={handleInputChange} handleNext={handleNext} handleBack={handleBack} />;
      case 5:
        return <Step5 formData={formData} handleNext={handleNext} handleBack={handleBack} />;
      case 6:
        return <Step6 formData={formData} handleNext={handleNext} handleBack={handleBack} />;
      case 7:
        return <Step7 formData={formData} handleNext={handleNext} handleBack={handleBack} />;
      case 8:
        return <Step8 formData={formData} handleNext={handleNext} handleBack={handleBack} />;
      case 9:
        return <Step9 formData={formData} handleInputChange={handleInputChange} handleNext={handleNext} handleBack={handleBack} />;
      case 10:
        return <Step10 formData={formData} handleNext={handleNext} handleBack={handleBack} />;
      case 11:
        return <Step11 formData={formData} handleInputChange={handleInputChange} handleNext={handleNext} handleBack={handleBack} />;
      case 12:
        return <Step12 formData={formData} handleNext={handleNext} handleBack={handleBack} />;
      case 13:
        return <Step13 />;
      default:
        return <Step1 formData={formData} handleInputChange={handleInputChange} handleNext={handleNext} />;
    }
  };


  return (
    <div>
      <ProgressBar progress={progress} />
      {renderStep()}
    </div>
  );
};

export default RegisterForm;
