import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAccountDetails } from '../backend/api';

const Dashboard = () => {
    const [user, setUser] = useState({ email: '', name: '' });
    const navigate = useNavigate();

    useEffect(() => {
        getAccountDetails()
            .then(data => {
                setUser({ email: data.data.email, name: data.data.username });
            })
            .catch(error => {
                console.error('Errore nel recupero dei dettagli dell\'account:', error);
                navigate('/account/login');
            });
    }, [navigate]);

    const softwareLinks = [
        { name: 'Sales Hub', url: 'https://app.huberway.com' },
        { name: 'Marketing Hub', url: 'https://campaign.huberway.com' },
        { name: 'Totem', url: 'https://totem-ps.huberway.com', restricted: true },
        { name: 'Web Analytics', url: 'https://analytics.huberway.com' }
    ];

    const canAccessTotem = user.email === 'direzione@psicopatici.com';

    return (
        <div className="dashboard-container">
            <header className="header">
                <img src="https://cdn.huberway.com/site/logo-dark.svg" style={{ height: "40px" }} alt="Huberway Logo" className="logo" />
                <div className="user-info">
                    Hello, {user.name}
                </div>
            </header>
            <main className="software-list dynamic">
                {softwareLinks.map((software) => (
                    (!software.restricted || canAccessTotem) && (
                        <a key={software.name} href={software.url} className="software-card dynamic-card">
                            <div className="card-content">
                                <h3>{software.name}</h3>
                                <p>Access {software.name} tools and services</p>
                            </div>
                            <div className="card-hover-effect"></div>
                        </a>
                    )
                ))}
            </main>
        </div>
    );
};

export default Dashboard;
